CREATE TABLE "_GR_RCV_SHIPMENT_HEADERS" AS(
SELECT 'GR_'||CAST(RSH.SHIPMENT_HEADER_ID AS  VARCHAR(100)) AS "OBJECT_ID"
  , RSH.SHIPMENT_NUM AS NUMBER
FROM "RCV_SHIPMENT_HEADERS" RSH
WHERE 1=1
);

